mapper.update:
update tst_class_banner
set banner_img=#{banner_img}
,banner_url=#{banner_url},
sort=#{sort}
where banner_id=#{banner_id}

update tst_class_banner
set is_delete='1'
where banner_id=#{banner_id}

mapper.select:
select * from tst_class_banner
where is_delete='0' and class_id=#{class_id}
order by sort desc

mapper.update:
update tst_activity_album_goods
set is_delete='1'
where album_id=#{album_id} and goods_id=#{goods_id}

update tst_activity_album
set ablum_img=#{ablum_img},
ablum_name=#{ablum_name},ablum_desc=#{ablum_desc},
sort=#{sort}
where album_id=#{album_id}

update tst_activity_album
set is_delete='1'
where album_id=#{album_id}
